import React, { useEffect, useState } from "react";

const Top=(props)=>{
    const {top}=props;
    const [inform,setInform]=useState([]);
    useEffect(()=>{
        getInform();
    })
    const getInform=async()=>{
        const result=await axios.get(`https://api.themoviedb.org/3/movie/${top.id}?api_key=2b9911b777000a14126dc1f921515408&append_to_response=videos`);
        console.log(result);
        setInform(result);
      }

    const url="http://image.tmdb.org/t/p/w185";
    return(
        <>
           {
               top.map((topmovie)=>(
                <div className="center-box">
                <img src={url + topmovie.poster_path} alt="top"/>
            </div> 
               ))
           }

        </>
    );
}
export default Top;
