import React from "react";


const Info=(props)=>{
    const {info1}=props;
    const url="http://image.tmdb.org/t/p/w185";
    return(
        <>
        
            <div className="row">
                 {
                    info1.map((val)=>(
                            <div className="col-md-3 my-4">
                                <div className="card">
                                    <img src={ url + val.poster_path} alt="movie"/>
                                    <div className="card-title my-2 ">
                                    <h5>{val.title}</h5>
                                    </div>
                                    <div className="card-body text-justify">
                                    <p>{val.overview}</p>
                                    <button className="btn btn-dark" onClick={val.hompage}>View</button>
                                    </div>
                                </div>
                          </div>
                    ))
                }
            </div>
    
        </>
    );
}
export default Info;
