import React, { useEffect, useState } from "react";
import axios from "axios";

const Top=(props)=>{
    const {top}=props;
    const [inform,setInform]=useState([]);
    useEffect(()=>{
    
    })
    const getInform=async(e)=>{
        console.log(e.currentTarget.dataset.div_id);
        const result=await axios.get(`https://api.themoviedb.org/3/movie/${e.currentTarget.dataset.div_id}?api_key=2b9911b777000a14126dc1f921515408&append_to_response=videos`);
        setInform(result.data);
      }

    const url="http://image.tmdb.org/t/p/w185";
    return(
        <>
           {
               top.map((topmovie)=>(
                <div className="center-box" onClick={getInform} data-div_id={topmovie.id}>
                <img src={url + topmovie.poster_path} alt="top" />
            </div> 
               ))
           }

        </>
    );
}
export default Top;
