import React, { useState } from 'react';
import axios from "axios";
import { NavLink,Switch,Route} from 'react-router-dom';
import Info from "./Info";

const Movies=(props)=>{
 const [info,setInfo]=useState([]);
 const {movie}=props;
 const url="http://image.tmdb.org/t/p/w185"

 const getInfo=async(e)=>{
    const result=await axios.get(`https://api.themoviedb.org/3/movie/${e.target.value}?api_key=2b9911b777000a14126dc1f921515408&append_to_response=videos`);
    console.log(result);
    setInfo(result.data);
  }

return(
  <>
  <Switch>
    <Route exact path="/" component={App}/>
    <Route exact path="/info" component={() => <Info info1={info}/>}/>
  </Switch>
  
  <div className="row ">
      {
          movie.map((val)=>(
                <div className="col-md-3 my-4">
                    <div className="card">
                        <img src={ url + val.poster_path} alt="movie"/>
                        <div className="card-title my-2 ">
                        <h5>{val.title}</h5>
                        </div>
                        <div className="card-body text-justify">
                        <p>{val.overview}</p>
                        <NavLink to={{pathname:"/info", state : {info1:info}}}>
                        <button className="btn btn-dark" value={val.id} onClick={getInfo}>View</button>
                        </NavLink>
                        </div>
                    </div>
                </div>
          ))
      }
  </div>
  </>
);
}
export default Movies;
