import React, { useEffect, useState } from "react";
import axios from "axios";

const Info=(props)=>{
    
    const info1=props.location.state;
    const [info,setInfo]=useState([]);
    const url="http://image.tmdb.org/t/p/w185";
    
    useEffect(()=>{
        getInfo();
    },[])
    const getInfo=async(e)=>{
        const result=await axios.get(`https://api.themoviedb.org/3/movie/${info1.id}?api_key=2b9911b777000a14126dc1f921515408&append_to_response=videos`);
        console.log(result.data);
        setInfo(result.data);
      }

    
    return(
        <>
           <div className="container">
           <div className="row ">
            {
          info.map((val)=>(
                <div className="col-md-3 my-4">
                    <div className="card">
                        <img src={ url + val.poster_path} alt="movie"/>
                        <div className="card-title my-2 ">
                        <h5>{val.title}</h5>
                        </div>
                        <div className="card-body text-justify">
                        <p>{val.overview}</p>
                        <button className="btn btn-dark"> 
                        View </button>
                        </div>
                    </div>
                </div>
          ))
      }
  </div>
           </div>
    
        </>
    );
}
export default Info;
