import React from "react";

const Info=(props)=>{
    const {info}=props;
    const url="http://image.tmdb.org/t/p/w185";
    return(
        <>
        <div className="container">
            <div className="row">
                            <div className="col-md-3 my-4">
                                <div className="card">
                                   
                                    <div className="card-title my-2 ">
                                    <h5>{info.title}</h5>
                                    </div>
                                    <div className="card-body text-justify">
                                    <p>{info.overview}</p>
                                    <button className="btn btn-dark" onClick={info.hompage}>View</button>
                                    </div>
                                </div>
                           </div>
            </div>
        </div>
        </>
    );
}
export default Info;
