import React, { useEffect, useState } from 'react';
import axios from "axios";
import Nav from './Components/Nav';
import Banner from './Components/Banner';
import Movies from './Components/Movies';

const App=()=>{
const [search ,setSearch]=useState("dark");
const [movie, setMovie]=useState([])
const inputEvent=(e)=>{
  setSearch(e.target.value);
}

const API_Key="2b9911b777000a14126dc1f921515408";

useEffect(()=>{
  getMovies();
},[]);
const getMovies=async()=>{
  const result=await axios.get(`https://api.themoviedb.org/3/search/movie?api_key=${API_Key}&query=${search}`);
  console.log(result);
  setMovie(result);
}
return(
  <>
  <Nav/>
  <Banner search={search} inputEvent={inputEvent}/>
  <Movies/>
  </>
);
}
export default App;
